var link=document.querySelector(".btn-contacts"),popup=document.querySelector(".modal-content"),close=popup.querySelector(".modal-content-close"),form=popup.querySelector("form"),author=popup.querySelector("[name=writer-name]"),mail=popup.querySelector("[name=writer-email]"),text=popup.querySelector("[name=writer-text]");link.addEventListener("click",function(a){a.preventDefault(),popup.classList.add("modal-content-show")}),close.addEventListener("click",function(a){a.preventDefault(),popup.classList.remove("modal-content-show"),popup.classList.remove("modal-error")}),form.addEventListener("submit",function(a){author.value&&mail.value&&text.value?(a.preventDefault(),popup.classList.remove("modal-content-show"),popup.classList.remove("modal-error")):(a.preventDefault(),popup.classList.add("modal-error"))}),window.addEventListener("keydown",function(a){27===a.keyCode&&popup.classList.contains("modal-content-show")&&popup.classList.remove("modal-content-show")});